// Extract webhook data and prepare OpenRouter request
const webhookData = $input.first().json;
console.log('Webhook Data:', JSON.stringify(webhookData, null, 2));

const userMessage = webhookData.body.input.message;
const chatId = webhookData.body.input.chat_id;
const userId = webhookData.body.session_variables['x-hasura-user-id'];

console.log('User Message:', userMessage);
console.log('Chat ID:', chatId);
console.log('User ID:', userId);

// Prepare OpenRouter API request with FREE model
const openRouterRequest = {
  model: "meta-llama/llama-3.1-8b-instruct:free",
  messages: [
    {
      role: "system",
      content: "You are a helpful AI assistant. Be concise and friendly in your responses."
    },
    {
      role: "user",
      content: userMessage
    }
  ],
  max_tokens: 150,
  temperature: 0.7
};

console.log('OpenRouter Request:', JSON.stringify(openRouterRequest, null, 2));

return {
  openRouterRequest: openRouterRequest,
  chatId: chatId,
  userMessage: userMessage,
  userId: userId
};

// Process OpenRouter response and extract AI content
const openRouterResponse = $input.first().json;
const requestData = $('Prepare Request').first().json;

console.log('OpenRouter Response:', JSON.stringify(openRouterResponse, null, 2));
console.log('Request Data:', JSON.stringify(requestData, null, 2));

// Extract AI content (handle both array and object responses)
let aiContent;
if (Array.isArray(openRouterResponse)) {
  aiContent = openRouterResponse[0]?.choices?.[0]?.message?.content;
} else {
  aiContent = openRouterResponse?.choices?.[0]?.message?.content;
}

const chatId = requestData.chatId;
const userMessage = requestData.userMessage;

console.log('Extracted AI Content:', aiContent);
console.log('Chat ID:', chatId);

if (!aiContent) {
  throw new Error('Failed to extract AI content from OpenRouter response');
}

// Prepare data for database save
return {
  chatId: chatId,
  aiContent: aiContent,
  userMessage: userMessage,
  success: true,
  message: 'Response generated successfully',
  response: aiContent
};

