parameters.jsCode:
// Extract the AI response and save to database
const openRouterResponse = $input.first().json;
const webhookData = $('Webhook Trigger').first().json;

console.log('OpenRouter Response:', JSON.stringify(openRouterResponse));
console.log('Webhook Data:', JSON.stringify(webhookData));

// Extract AI content (handle both array and object responses)
let aiContent;
if (Array.isArray(openRouterResponse)) {
  aiContent = openRouterResponse[0]?.choices?.[0]?.message?.content;
} else {
  aiContent = openRouterResponse?.choices?.[0]?.message?.content;
}

const chatId = webhookData.body.input.chat_id;
const userMessage = webhookData.body.input.message;

console.log('Extracted AI Content:', aiContent);
console.log('Chat ID:', chatId);

if (!aiContent) {
  throw new Error('Failed to extract AI content from OpenRouter response');
}

// Save to database
const graphqlQuery = {
  query: `mutation InsertBotMessage($chatId: uuid!, $content: String!) { 
    insert_messages_one(object: { chat_id: $chatId, content: $content, is_bot: true }) { 
      id 
    } 
  }`,
  variables: {
    chatId: chatId,
    content: aiContent
  }
};

console.log('GraphQL Query:', JSON.stringify(graphqlQuery));

// Make the GraphQL request
const dbResponse = await fetch('https://wertssqowjrkykmeovsg.hasura.ap-south-1.nhost.run/v1/graphql', {
  method: 'POST',
  headers: {
    'Content-Type': 'application/json',
    'x-hasura-admin-secret': 'Hq=1ILX8*V%Df8PlCyljd*BVys-::p,A'
  },
  body: JSON.stringify(graphqlQuery)
});

const dbResult = await dbResponse.json();
console.log('Database Save Result:', JSON.stringify(dbResult));

if (dbResult.errors) {
  throw new Error('Failed to save bot message: ' + JSON.stringify(dbResult.errors));
}

// Return success response
return {
  success: true,
  message: 'Response generated successfully',
  response: aiContent
};

